mapper.select:
select a.user_id as userId, a.follower_id as followerId, u1.nickname as userName, u1.avatar as userAvatar, u2.nickname as followerName, u2.avatar as followerAvatar from biz_user_follow as a left join biz_user as u1 on u1.id = user_id left join biz_user as u2 on u2.id = a.follower_id where a.del_flag=0
and a.user_id= #{userId}
and a.follower_id= #{followerId}

mapper.update:
update biz_user_follow
set
del_flag=1
where user_id =
#{userId}
and follower_id = #{followerId}
